import React from 'react';
import './App.css';
import CardList from './CardList';
import Form from './Form';

const testData = [
  {
    id: '33434343',
    name: 'Dan Abramov',
    avatar_url: 'https://avatars0.githubusercontent.com/u/810438?v=4',
    company: '@facebook'
  },
  {
    name: 'Sophie Alpert',
    avatar_url: 'https://avatars2.githubusercontent.com/u/6820?v=4',
    company: 'Humu'
  },
  {
    name: 'Sebastian Markbåge',
    avatar_url: 'https://avatars2.githubusercontent.com/u/63648?v=4',
    company: 'Facebook'
  }
];

class App extends React.Component {
  state = {
    profiles: testData,
    userName: ''
  };

  handleSubmit = async e => {
    // e.preventDefault();
    const url = await fetch(`https://api.github.com/users/${this.state.userName}`);
    const resp = await url.json();
    this.props.onSubmit(resp);
    this.setState({ userName: '' });
    console.log('submitted');
  };

  handleFormChange = event => {
    this.setState({ userName: event.target.value });
  };

  addNewProfile = profileData => {
    console.log('profileData', profileData);
    this.setState(prevState => ({
      profiles: [profileData, ...prevState.profiles]
    }));
  };

  componentDidMount() {
    this.handleSubmit();
  }

  render() {
    return (
      <div className="App">
        <Form onSubmit={this.addNewProfile} userName={this.state.userName} handleFormChange={this.handleFormChange} />
        <CardList profiles={this.state.profiles} />
      </div>
    );
  }
}

export default App;
